import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import '../styles/Resources.css';

function Resources() {
  const [url, setUrl] = useState('');
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState("Ted Talk Summarization");
  const [guideContent, setGuideContent] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSummary(null);

    try {
      const response = await fetch('/summarize-video', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url }),
      });

      if (!response.ok) {
        throw new Error("Failed to summarize video. Please check the URL or try again.");
      }

      const data = await response.json();
      setSummary(data.summary);
      setUrl(''); // Clear URL input after submission
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
    setGuideContent(null);
    setLoading(false); // Ensure loading is off for static content
    setError(null);
  };

  return (
    <div className="resources-page">
      {/* Navbar */}
      <header className="navbar">
      <Link to="/home"><img src="grow_to_impress_logo.png" alt="logo" className="logo" /></Link>
        <div className="navbar-links">
          <Link to="/how-to-section" className="nav-link">LEARN HOW TO</Link>
          <Link to="/stories" className="nav-link">STORIES OF INSPIRATION</Link>
          <Link to="/resources" className="nav-link active">RESOURCES</Link>
          <Link to="/chatbot" className="nav-link">IXIA</Link>
        </div>
      </header>

      {/* Content Container */}
      <div className="resources-content-container">
        <div className="topics-list">
          <ul>
            <li
              className={selectedTopic === "Ted Talk Summarization" ? "selected" : ""}
              onClick={() => setSelectedTopic("Ted Talk Summarization")}
            >
              Ted Talk Summarization
            </li>
            <li
              className={selectedTopic === "Mentorship" ? "selected" : ""}
              onClick={() => handleTopicSelect("Mentorship")}
            >
              Mentorship
            </li>
            <li
              className={selectedTopic === "Women in Management" ? "selected" : ""}
              onClick={() => handleTopicSelect("Women in Management")}
            >
              Women in Management
            </li>
            <li
              className={selectedTopic === "Scholarships" ? "selected" : ""}
              onClick={() => handleTopicSelect("Scholarships")}
            >
              Scholarships
            </li>
            <li
              className={selectedTopic === "Additional Google Resources" ? "selected" : ""}
              onClick={() => handleTopicSelect("Additional Google Resources")}
            >
              Additional Google Resources
            </li>
          </ul>
        </div>

        <div className="main-content">
          {/* TED Talk Summarization Section */}
          {selectedTopic === "Ted Talk Summarization" && (
            <div className="submission-area">
              <h2>Submit a TED Talk for Summarization</h2>
              <form onSubmit={handleSubmit}>
                <div className="input-group">
                  <input
                    type="url"
                    placeholder="Enter TED Talk URL"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    required
                  />
                  <button type="submit" disabled={loading}>Submit</button>
                </div>
              </form>

              {loading && <p className="loading-message">Loading summary...</p>}
              {error && <p className="error-message">{error}</p>}
              {summary && (
                <div className="summary-result">
                  <h3>Video Summary</h3>
                  <ReactMarkdown>{summary}</ReactMarkdown>
                </div>
              )}
            </div>
          )}

          {/* Guide Content Section */}
          {selectedTopic !== "Ted Talk Summarization" && loading && <p className="loading-message">Loading {selectedTopic} guide...</p>}
          {guideContent && (
            <div className="guide-content">
              <h3>{selectedTopic} Guide</h3>
              <ReactMarkdown>{guideContent}</ReactMarkdown>
            </div>
          )}

          {/* Additional Google Resources Section */}
          {selectedTopic === "Additional Google Resources" && (
            <div className="google-resources-content">
              <h3>Additional Google Resources</h3>
              <p>
                Learn Faster, Study Smarter! Our product uses Gemini, Google's super smart AI, to help you with your journey! Gemini can answer your questions, summarize notes, and even turn your PDFs into podcasts! ðŸ¤¯
              </p>
              <p>Check out <a href="https://notebooklm.google.com/" target="_blank" rel="noopener noreferrer">NotebookLM</a> and the <a href="https://gemini.google.com/app" target="_blank" rel="noopener noreferrer">Gemini</a> platform for more awesome AI tools. âœ¨</p>
            </div>
          )}

          {error && <p className="error-message">{error}</p>}
        </div>
      </div>
    </div>
  );
}

export default Resources;
