// src/pages/StoryDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/StoryDetail.css';

function StoryDetail() {
  const { id } = useParams();
  const [story, setStory] = useState(null);

  useEffect(() => {
    const fetchStory = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/story/${id}`);
        setStory(response.data);
      } catch (error) {
        console.error("Failed to fetch story", error);
      }
    };
    fetchStory();
  }, [id]);

  if (!story) return <p>Loading story...</p>;

  const imageUrl = story.image ? story.image : null;

  return (
    <div className="story-detail-page">
      <header className="navbar">
        <Link to="/stories" className="back-link">‚Üê Back to Stories</Link>
      </header>

      <div className="story-detail-container">
        {imageUrl && <img src={imageUrl} alt="User submitted" />}
        <h1>{story.name}</h1>
        <p>{story.story}</p>
      </div>
    </div>
  );
}

export default StoryDetail;
