import React, { useState } from 'react';
import api from '../axiosConfig'; // Import the API instance
import { useNavigate } from 'react-router-dom';
import '../styles/Signup.css';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    console.log("Attempting signup with:", { email, password });

    try {
      const response = await api.post('/signup', { email, password });
      console.log("Signup response:", response);

      if (response.status === 201) {
        setMessage('Signup successful! Redirecting to login...');
        setTimeout(() => navigate('/login'), 2000);
      }
    } catch (error) {
      console.error("Signup error:", error);
      setError(error.response?.data?.error || 'Signup failed.');
    }
  };

  return (
    <div className="container">
      <div className="left-section">
        <h2>Welcome to GROW TO IMPRESS – Join Us Today!</h2>
        <p>We’re excited to have you on board. Together, let's start your journey to growth and success.</p>
        <img src="/path/to/decorative-flowers.png" alt="Decorative flowers" className="decorative-flowers" />
      </div>
      <div className="right-section">
        <form onSubmit={handleSignup}>
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Your Username"
            required
          />
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Your Email"
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Your Password"
            required
          />
          <label>Confirm Password</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
            required
          />
          <button type="submit">Sign Up</button>
          {error && <p className="error">{error}</p>}
          {message && <p className="message">{message}</p>}
          <div className="login-link">
            <p>Already have an account? <a href="/login">Log in</a></p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
