import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/HowToSection.css';

function HowToSection({ userId }) { // Ensure userId is passed as a prop
  const [selected, setSelected] = useState("Building Your Club");
  const [guide, setGuide] = useState([]);
  const [checkedDays, setCheckedDays] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentDayIndex, setCurrentDayIndex] = useState(0);

  const topics = [
    "Building Your Club",
    "Getting Certifications and Courses",
    "Building Confidence",
    "Recognizing Healthy Relationships",
    "Saving Your First $1,000",
    "Improving Communication Skills"
  ];

  // Fetch user progress and guide data when the topic changes
  const fetchGuideAndProgress = async (goalIndex) => {
    setLoading(true);
    const selectedTopic = topics[goalIndex];

    try {
      // Fetch the guide for the selected topic
      const guideResponse = await axios.get(`/get-guide/${selectedTopic}`);
      if (guideResponse.data.daily_guide) {
        setGuide(guideResponse.data.daily_guide.guide);

        // Fetch user's progress on this topic
        const progressResponse = await axios.post('/get-user-progress', { user_id: userId });
        const userProgress = progressResponse.data.progress;

        // Retrieve completed days for this topic
        const completedDays = userProgress[selectedTopic]
          ? userProgress[selectedTopic].filter(day => day.completed).map(day => day.day)
          : [];

        setCheckedDays(completedDays);

        // Set to the first incomplete day if it exists, otherwise show the last day
        const firstIncompleteDayIndex = guideResponse.data.daily_guide.guide.findIndex(
          day => !completedDays.includes(day.day)
        );
        setCurrentDayIndex(firstIncompleteDayIndex >= 0 ? firstIncompleteDayIndex : guide.length - 1);
      } else {
        console.error("Guide not found for the topic");
      }
    } catch (error) {
      console.error("Error fetching guide or progress:", error);
    }
    setLoading(false);
  };

  useEffect(() => {
    const goalIndex = topics.indexOf(selected);
    fetchGuideAndProgress(goalIndex);
  }, [selected, userId]);

  const handleCheckboxChange = async (day) => {
    setCheckedDays((prev) =>
      prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
    );
    setCurrentDayIndex((prevIndex) => Math.min(prevIndex + 1, guide.length - 1));

    // Update completion status in the database
    try {
      await axios.post('/update-day-completion', { user_id: userId, topic: selected, day, completed: true });
    } catch (error) {
      console.error("Failed to update day completion status:", error);
    }
  };

  const handlePrevDay = () => {
    setCurrentDayIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleNextDay = () => {
    setCurrentDayIndex((prevIndex) => Math.min(prevIndex + 1, guide.length - 1));
  };

  const currentDay = guide[currentDayIndex];

  return (
    <div className="how-to-section">
      {/* Header Section */}
      <div className="header">
        <Link to="/dashboard" className="back-button">‚Üê Back to Dashboard</Link>
        <h1>How-To Section</h1>
        <p>Learn essential life skills step by step</p>
      </div>

      {/* Main Content Area */}
      <div className="content-container">
        {/* Sidebar - Topics List */}
        <div className="topics-list">
          <ul>
            {topics.map(topic => (
              <li
                key={topic}
                onClick={() => { setSelected(topic); setCurrentDayIndex(0); setCheckedDays([]); }}
                className={selected === topic ? "selected" : ""}
              >
                {topic}
              </li>
            ))}
          </ul>
        </div>

        {/* Guide Content Section */}
        <div className="main-content">
          <h2>{selected}: 21-Day Plan</h2>
          {loading ? (
            <p>Loading guide...</p>
          ) : currentDay ? (
            <div className={`day-item ${checkedDays.includes(currentDay.day) ? 'completed' : ''}`}>
              <div className="day-title">
                <input
                  type="checkbox"
                  checked={checkedDays.includes(currentDay.day)}
                  onChange={() => handleCheckboxChange(currentDay.day)}
                />
                <span>Day {currentDay.day}: {currentDay.title}</span>
              </div>
              <ul className="approaches">
                {currentDay.approaches.map((approach, index) => (
                  <li key={index}>{approach}</li>
                ))}
              </ul>
              <div className="navigation-buttons">
                <button onClick={handlePrevDay} disabled={currentDayIndex === 0}>Previous Day</button>
                <button onClick={handleNextDay} disabled={currentDayIndex === guide.length - 1}>Next Day</button>
              </div>
            </div>
          ) : (
            <p>All days completed! üéâ</p>
          )}
          <p className="growth-progress">Growth Progress</p>
        </div>
      </div>
    </div>
  );
}

export default HowToSection;
