// src/components/HowToSection.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../HowToSection.css'; // Add styles in this file

function HowToSection() {
  const navigate = useNavigate();

  const steps = [
    "Step 1", "Step 2", "Step 3", "Step 4", "Step 5", "Step 6", "Step 7", "Step 8", "Step 9"
  ];

  const handleReturnToDashboard = () => {
    navigate('/dashboard');
  };

  return (
    <div className="how-to-section">
      <button onClick={handleReturnToDashboard} className="return-button">
        Return to Dashboard
      </button>

      <h1>How-To Section</h1>
      <p>Learn essential life skills step by step</p>

      <div className="content-container">
        <div className="sidebar">
          <ul>
            <li className="selected">Building Your Club</li>
            <li>Getting Certifications and Courses</li>
            <li>Building Confidence</li>
            <li>Recognizing Healthy Relationships</li>
            <li>Saving Your First $1,000</li>
            <li>Improving Communication Skills</li>
          </ul>
        </div>

        <div className="main-content">
          <h2>Building Your Club</h2>
          <div className="steps">
            {steps.map((step, index) => (
              <label key={index}>
                <input type="checkbox" defaultChecked={index % 2 === 0} /> {step}
              </label>
            ))}
          </div>
          <p className="growth-progress">Growth Progress</p>
        </div>

        <div className="image-section">
          <img src="/path/to/decorative-flowers.png" alt="Decorative flowers" />
        </div>
      </div>
    </div>
  );
}

export default HowToSection;
