// src/components/HowToSection.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HowToSection.css';

function HowToSection() {
  const [selected, setSelected] = useState("Building Your Club");

  const topics = [
    "Building Your Club",
    "Getting Certifications and Courses",
    "Building Confidence",
    "Recognizing Healthy Relationships",
    "Saving Your First $1,000",
    "Improving Communication Skills"
  ];

  const steps = Array.from({ length: 9 }, (_, i) => `Step ${i + 1}`);

  return (
    <div className="how-to-section">
      {/* Header Section */}
      <div className="header">
        <Link to="/dashboard" className="back-button">‚Üê Back to Dashboard</Link>
        <h1>How-To Section</h1>
        <p>Learn essential life skills step by step</p>
      </div>

      {/* Main Content Area */}
      <div className="content-container">
        {/* Sidebar - Topics List */}
        <div className="topics-list">
          <ul>
            {topics.map(topic => (
              <li
                key={topic}
                onClick={() => setSelected(topic)}
                className={selected === topic ? "selected" : ""}
              >
                {topic}
              </li>
            ))}
          </ul>
        </div>

        {/* Steps and Image Section */}
        <div className="main-content">
          <h2>{selected}</h2>
          <div className="steps">
            {steps.map(step => (
              <div className="step-item" key={step}>
                <input type="checkbox" />
                <span>{step}</span>
              </div>
            ))}
          </div>
          <p className="growth-progress">Growth Progress</p>

          {/* Image Section */}
          <div className="image-section">
            <img src="/path/to/your-image.png" alt="Illustrative Image" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default HowToSection;
