import React, { useState } from 'react';
import api from '../axiosConfig'; // Assuming you've set up axiosConfig.js for baseURL configuration
import { useNavigate, Link } from 'react-router-dom';
import '../styles/Login.css'; // Specific styles for login

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      let response = await api.post('/', { email, password });
      if (response.status === 200) {
        alert('Login successful');
        navigate('/dashboard');
        return;
      }
    } catch (error) {
      console.log("Failed login at '/' endpoint. Trying '/login'.", error);
    }

    try {
      let response2 = await api.post('/login', { email, password });
      if (response2.status === 200) {
        alert('Login successful');
        navigate('/dashboard');
      } else {
        setError('Invalid credentials');
      }
    } catch (error) {
      console.error("Login error:", error);
      setError('Invalid credentials');
    }
  };

  return (
    <div className="hero-section">
      <div className="hero-content">
        <h1>Welcome to Grow to Impress!</h1>
        <p>Start your journey towards growth and self-discovery with our vibrant community.</p>
        <form onSubmit={handleLogin} className="login-form">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Your Email"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Your Password"
            required
          />
          <button type="submit" className="cta-button">Sign In</button>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
}

export default Login;
