// Dashboard.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Dashboard.css';

function Dashboard() {
  const [isChatbotOpen, setIsChatbotOpen] = useState(false);

  const toggleChatbot = () => {
    setIsChatbotOpen(!isChatbotOpen);
  };

  return (
    <div className="dashboard-container">
      <h1>GROW IMPRESS</h1>
      <p>Your personal growth journey starts here. Learn, grow, and impress.</p>

      <div className="sections">
        <Link to="/how-to-section" className="section">
          <img src="vecteezy_hand-drawn-flat-design-simple-flower-outline_.jpg" alt="How To Section" />
          <p>HOW TO SECTION</p>
        </Link>
        <Link to="/stories" className="section">
          <img src="vecteezy_hand-drawn-flat-design-simple-flower-2_.jpg" alt="Stories of Inspiration" />
          <p>STORIES OF INSPIRATION</p>
        </Link>
        <Link to="/resources" className="section">
          <img src="vecteezy_handdrawn-spring-flowers-collection_.jpg" alt="Resources" />
          <p>RESOURCES</p>
        </Link>
      </div>

      <div className="chatbot-icon" onClick={toggleChatbot}>
        <img src="/path/to/chatbot-icon.png" alt="Chatbot" />
      </div>

      {/* Chatbot popup */}
      {isChatbotOpen && (
        <div className="chatbot-popup">
          <div className="chatbot-header">
            <h3>Chatbot</h3>
            <button onClick={toggleChatbot} className="close-chatbot">âœ–</button>
          </div>
          <div className="chatbot-content">
            <p>Hi there! How can I help you today?</p>
            {/* Additional chatbot functionalities can go here */}
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
