// src/components/StoriesOfInspiration.js
import React, { useState } from 'react';
import axios from 'axios';
import '../styles/StoriesOfInspiration.css';

function StoriesOfInspiration() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
    image: null,
  });
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare the form data for submission
    const data = new FormData();
    data.append('name', formData.name);
    data.append('surname', formData.surname);
    data.append('email', formData.email);
    data.append('message', formData.message);
    if (formData.image) {
      data.append('image', formData.image);
    }

    try {
      const response = await axios.post('/submit-story', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      if (response.status === 201) {
        setSuccessMessage('Story submitted successfully!');
        setFormData({
          name: '',
          surname: '',
          email: '',
          message: '',
          image: null,
        });
      }
    } catch (error) {
      setErrorMessage('Failed to submit story. Please try again.');
    }
  };

  return (
    <div className="submission-container">
      <h1>Share Your Story</h1>
      <p>Submit your inspiring story to be featured on our platform.</p>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="surname"
          placeholder="Surname"
          value={formData.surname}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <textarea
          name="message"
          placeholder="Your story"
          value={formData.message}
          onChange={handleChange}
          required
        ></textarea>
        <input
          type="file"
          name="image"
          onChange={handleFileChange}
          accept="image/*"
        />
        <button type="submit">Submit</button>
      </form>
      {successMessage && <p className="success-message">{successMessage}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

export default StoriesOfInspiration;
