// src/components/Resources.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';


function Resources() {
  const [url, setUrl] = useState('');
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSummary(null);

    try {
      const response = await fetch('/summarize-video', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url }),
      });

      if (!response.ok) {
        throw new Error("Failed to summarize video. Please check the URL or try again.");
      }

      const data = await response.json();
      setSummary(data.summary);
      setUrl('');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="how-to-section">
      <div className="header">
      <div className="back-container">
        <Link to="/dashboard" className="back-button">‚Üê Dashboard</Link>
       </div>
        <div className="header-content">
          <h1>GROW IMPRESS</h1>
          <p>Your personal growth journey starts here. Learn, grow, and impress</p>
        </div>
      </div>


      <div className="content-container">
        <div className="topics-list">
          <ul>
            <li>Ted Talk Summarization</li>
            <li>Mentorship</li>
            <li>Women in Management</li>
            <li>Scholarships</li>
          </ul>
        </div>

        <div className="main-content">
          <div className="submission-area">
            <h2>Submit a TED Talk for Summarization</h2>
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <input
                  type="url"
                  placeholder="Enter TED Talk URL"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  required
                />
                <button type="submit" disabled={loading}>Submit</button>
              </div>
            </form>

            {loading && <p className="loading-message">Loading summary...</p>}
            {error && <p className="error-message">{error}</p>}

            {summary && (
              <div className="summary-result">
                <h3>Video Summary</h3>
                <ReactMarkdown>{summary}</ReactMarkdown>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Resources;
