import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import '../styles/Resources.css';

function Resources() {
  const [url, setUrl] = useState('');
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedTopic, setSelectedTopic] = useState("Ted Talk Summarization"); // Set default topic

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSummary(null);

    try {
      const response = await fetch('/summarize-video', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url }),
      });

      if (!response.ok) {
        throw new Error("Failed to summarize video. Please check the URL or try again.");
      }

      const data = await response.json();
      setSummary(data.summary);
      setUrl('');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="how-to-section">
        <div className="content-container">
          <div className="topics-list">
            <ul>
              <li
                className={selectedTopic === "Ted Talk Summarization" ? "selected" : ""}
                onClick={() => setSelectedTopic("Ted Talk Summarization")}
              >
                Ted Talk Summarization
              </li>
              <li
                className={selectedTopic === "Mentorship" ? "selected" : ""}
                onClick={() => setSelectedTopic("Mentorship")}
              >
                Mentorship
              </li>
              <li
                className={selectedTopic === "Women in Management" ? "selected" : ""}
                onClick={() => setSelectedTopic("Women in Management")}
              >
                Women in Management
              </li>
              <li
                className={selectedTopic === "Scholarships" ? "selected" : ""}
                onClick={() => setSelectedTopic("Scholarships")}
              >
                Scholarships
              </li>
            </ul>
          </div>

          <div className="main-content">
            {selectedTopic === "Ted Talk Summarization" && (
              <div className="submission-area">
                <h2>Submit a TED Talk for Summarization</h2>
                <form onSubmit={handleSubmit}>
                  <div className="input-group">
                    <input
                      type="url"
                      placeholder="Enter TED Talk URL"
                      value={url}
                      onChange={(e) => setUrl(e.target.value)}
                      required
                    />
                    <button type="submit" disabled={loading}>Submit</button>
                  </div>
                </form>

                {loading && <p className="loading-message">Loading summary...</p>}
                {error && <p className="error-message">{error}</p>}

                {summary && (
                  <div className="summary-result">
                    <h3>Video Summary</h3>
                    <ReactMarkdown>{summary}</ReactMarkdown>
                  </div>
                )}
              </div>
            )}
            {/* Other content sections for different topics could be added here as needed */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Resources;
