import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/HowToSection.css';

function HowToSection() {
  const topics = [
    "Building Your Club",
    "Getting Certifications and Courses",
    "Building Confidence",
    "Recognizing Healthy Relationships",
    "Saving Your First $1,000",
    "Improving Communication Skills"
  ];

  const [selected, setSelected] = useState(0); // Initialize with the first topic index
  const [guide, setGuide] = useState([]); // State to store the generated guide
  const [checkedDays, setCheckedDays] = useState([]); // Track completed days
  const [loading, setLoading] = useState(false);

  // Fetch guide from backend based on selected topic index
  const fetchGuide = async (goalIndex) => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/how-to-section', { goalIndex });
      setGuide(response.data.guide); // Update guide with response
    } catch (error) {
      console.error("Failed to fetch guide:", error);
    }
    setLoading(false);
  };

  // Call fetchGuide whenever selected index changes
  useEffect(() => {
    fetchGuide(selected);
  }, [selected]);

  // Handle checkbox change
  const handleCheckboxChange = (day) => {
    setCheckedDays((prev) =>
      prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
    );
  };

  return (
    <div className="how-to-section">
      {/* Header Section */}
      <div className="header">
        <Link to="/dashboard" className="back-button">‚Üê Back to Dashboard</Link>
        <h1>How-To Section</h1>
        <p>Learn essential life skills step by step</p>
      </div>

      {/* Main Content Area */}
      <div className="content-container">
        {/* Sidebar - Topics List */}
        <div className="topics-list">
          <ul>
            {topics.map((topic, index) => (
              <li
                key={topic}
                onClick={() => setSelected(index)}
                className={selected === index ? "selected" : ""}
              >
                {topic}
              </li>
            ))}
          </ul>
        </div>

        {/* Guide Content Section */}
        <div className="main-content">
          <h2>{topics[selected]}: 21-Day Plan</h2>
          {loading ? (
            <p>Loading guide...</p>
          ) : (
            <div className="guide-content">
              {guide.map((dayItem) => (
                <div key={dayItem.day} className="day-item">
                  <div className="day-title">
                    <input
                      type="checkbox"
                      checked={checkedDays.includes(dayItem.day)}
                      onChange={() => handleCheckboxChange(dayItem.day)}
                    />
                    <span>Day {dayItem.day}: {dayItem.title}</span>
                  </div>
                  <ul className="approaches">
                    {dayItem.approaches.map((approach, index) => (
                      <li key={index}>{approach}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
          <p className="growth-progress">Growth Progress</p>
        </div>
      </div>
    </div>
  );
}

export default HowToSection;
