import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Signup.css';  // Make sure to create a CSS file for custom styles

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();

    // Basic validation
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/signup', {  // Ensure port matches your backend
        email,
        password,
      });

      if (response.status === 201) {
        setMessage('Signup successful! Redirecting to login...');
        setTimeout(() => navigate('/login'), 2000); // Redirect to login page after 2 seconds
      }
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setError(error.response.data.error); // Show backend error if available
      } else {
        setError('An unexpected error occurred. Please try again.');
      }
      console.error("Signup error:", error); // Log detailed error to console
    }
  };

  return (
    <div className="container">
      {/* Left Section */}
      <div className="left-section">
        <h2>Welcome to GROW TO IMPRESS – Join Us Today!</h2>
        <p>We’re excited to have you on board. Together, let's start your journey to growth and success.</p>
        <img src="/path/to/decorative-flowers.png" alt="Decorative flowers" className="decorative-flowers" />
      </div>

      {/* Right Section */}
      <div className="right-section">
        <form onSubmit={handleSignup}>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Your Email"
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Your Password"
            required
          />
          <label>Confirm Password</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Confirm Password"
            required
          />
          <button type="submit">Sign Up</button>
          {error && <p className="error">{error}</p>}
          {message && <p className="message">{message}</p>}
          <div className="login-link">
            <p>Already have an account? <a href="/login">Log in</a></p>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Signup;
