import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';  // Import your CSS file

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:80/login', {
        email,
        password,
      });
      if (response.status === 200) {
        alert('Login successful');
        navigate('/dashboard');  // Redirect to a dashboard or main page
      }
    } catch (error) {
      setError('Invalid credentials');
    }
  };

  return (
    <div className="container">
      {/* Left Section */}
      <div className="left-section">
        <h2>Welcome to GROW TO IMPRESS – Your Journey Begins Here!</h2>
        <p>We believe in the power of young women like you to lead, innovate, and achieve.</p>
        <p>Whether you’re here to build new skills, find guidance, or get inspired, know that this platform is crafted with heart and purpose. We’ve created every resource with you in mind, helping you unlock your full potential, one step at a time.</p>
        <img src="/path/to/decorative-flowers.png" alt="Decorative flowers" className="decorative-flowers" />
      </div>

      {/* Right Section */}
      <div className="right-section">
        <form onSubmit={handleLogin}>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Value"
            required
          />
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Value"
            required
          />
          <button type="submit">Sign In</button>
          {error && <p className="error">{error}</p>}
          <div className="forgot-password">
            <a href="#">Forgot password?</a>
          </div>
        </form>
        <img src="/path/to/decorative-flowers.png" alt="Decorative flowers" className="decorative-flowers" />
      </div>
    </div>
  );
}

export default Login;
