// src/components/HowToSection.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/HowToSection.css';

function HowToSection() {
  const [selected, setSelected] = useState("Building Your Club");
  const [guide, setGuide] = useState([]); // State to store the generated guide
  const [checkedDays, setCheckedDays] = useState([]); // Track completed days
  const [loading, setLoading] = useState(false);
  const [currentDayIndex, setCurrentDayIndex] = useState(0); // Track the current day

  const topics = [
    "Building Your Club",
    "Getting Certifications and Courses",
    "Building Confidence",
    "Recognizing Healthy Relationships",
    "Saving Your First $1,000",
    "Improving Communication Skills"
  ];

  // Fetch the guide data for the selected topic
  const fetchGuide = async (goalIndex = 0) => {
    setLoading(true);
    const selectedTopic = topics[goalIndex];

    try {
      const response = await axios.get(`/get-guide/${selectedTopic}`);
      if (response.data.guide.length > 0) {
        setGuide(response.data.guide); // Update guide with stored data
        // Set the current day to the first incomplete day
        const firstIncompleteDay = response.data.guide.findIndex(day => !day.completed);
        setCurrentDayIndex(firstIncompleteDay >= 0 ? firstIncompleteDay : 0);
        setCheckedDays(response.data.guide.filter(day => day.completed).map(day => day.day));
      } else {
        const genResponse = await axios.post('/generate-plan', { goal_index: goalIndex });
        setGuide(genResponse.data.daily_guide.guide);
        setCurrentDayIndex(0);
      }
    } catch (error) {
      console.error("Failed to fetch or generate guide:", error);
    }
    setLoading(false);
  };

  // Fetch guide when selected topic changes or on initial load with index 0
  useEffect(() => {
    const goalIndex = topics.indexOf(selected);
    fetchGuide(goalIndex);
  }, [selected]);

  // Toggle checkbox status and move to the next day
  const handleCheckboxChange = async (day) => {
    setCheckedDays((prev) =>
      prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
    );
    setCurrentDayIndex((prevIndex) => Math.min(prevIndex + 1, guide.length - 1)); // Move to the next day

    // Update completion status in the database
    try {
      await axios.post('/update-day-completion', { topic: selected, day, completed: true });
    } catch (error) {
      console.error("Failed to update day completion status:", error);
    }
  };

  const handlePrevDay = () => {
    setCurrentDayIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleNextDay = () => {
    setCurrentDayIndex((prevIndex) => Math.min(prevIndex + 1, guide.length - 1));
  };

  const currentDay = guide[currentDayIndex];

  return (
    <div className="how-to-section">
      {/* Header Section */}
      <div className="header">
        <Link to="/dashboard" className="back-button">‚Üê Back to Dashboard</Link>
        <h1>How-To Section</h1>
        <p>Learn essential life skills step by step</p>
      </div>

      {/* Main Content Area */}
      <div className="content-container">
        {/* Sidebar - Topics List */}
        <div className="topics-list">
          <ul>
            {topics.map(topic => (
              <li
                key={topic}
                onClick={() => { setSelected(topic); setCurrentDayIndex(0); setCheckedDays([]); }}
                className={selected === topic ? "selected" : ""}
              >
                {topic}
              </li>
            ))}
          </ul>
        </div>

        {/* Guide Content Section */}
        <div className="main-content">
          <h2>{selected}: 21-Day Plan</h2>
          {loading ? (
            <p>Loading guide...</p>
          ) : currentDay ? (
            <div className="day-item">
              <div className="day-title">
                <input
                  type="checkbox"
                  checked={checkedDays.includes(currentDay.day)}
                  onChange={() => handleCheckboxChange(currentDay.day)}
                />
                <span>Day {currentDay.day}: {currentDay.title}</span>
              </div>
              <ul className="approaches">
                {currentDay.approaches.map((approach, index) => (
                  <li key={index}>{approach}</li>
                ))}
              </ul>
              <div className="navigation-buttons">
                <button onClick={handlePrevDay} disabled={currentDayIndex === 0}>Previous Day</button>
                <button onClick={handleNextDay} disabled={currentDayIndex === guide.length - 1}>Next Day</button>
              </div>
            </div>
          ) : (
            <p>All days completed! üéâ</p>
          )}
          <p className="growth-progress">Growth Progress</p>
        </div>
      </div>
    </div>
  );
}

export default HowToSection;
